import React, { Component } from "react";

class TableBody extends Component {
  render() {
    const { items, columns } = this.props;
    return (
      <tbody>
        {items.map((item) => (
          <tr key={item._id}>
            {columns.map((column) => (
              <th key={item._id}>{item[column.path]}</th>
            ))}
          </tr>
        ))}
      </tbody>
    );
  }
}

export default TableBody;

