function paginate(items, pageSize, activePage) {
  let startIndex = (pageSize - 1) * activePage;
  let movies = [];
  let movieIndex = 0;
  for (let i = startIndex; i < startIndex + pageSize; i++) {
    movies[movieIndex++] = items[i];
  }
  return movies;
}

export default paginate;
