import React, { Component } from "react";
import { getMovies } from "./services/fakeMovieService";
import Like from "./common/like";
import Pagination from "./pagination";

class Movies extends Component {
  state = {
    movies: getMovies(),
    pageSize: 4,
  };

  handleDelete = (movie) => {
    let movies = [...this.state.movies];
    movies = movies.filter((m) => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLike = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  handlePage = (pageNumber) => {
    console.log(pageNumber);
    let movies = [];
    pageNumber *= this.state.pageSize;
    for (let i = pageNumber - 1; i < pageNumber * this.state.pageSize; i++)
      movies[i] = this.state.movies[i];
    console.log(movies);

    return movies;
  };

  render() {
    let { length: count } = this.state.movies;
    if (count < 1) return <p>There are no movies in the database</p>;

    return (
      <React.Fragment>
        <p>Showing {count} movies</p>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th scope="col">Genre</th>
              <th scope="col">Stock</th>
              <th scope="col">Rate</th>
              <th />
              <th />
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((m) => (
              <tr key={m._id}>
                <th scope="row">{m.title}</th>
                <td>{m.genre.name}</td>
                <td>{m.numberInStock}</td>
                <td>{m.dailyRentalRate}</td>
                <td>
                  <Like movie={m} onLike={this.handleLike} />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => this.handleDelete(m)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={count}
          pageSize={this.state.pageSize}
          onPageChange={this.handlePage}
        />
      </React.Fragment>
    );
  }
}

export default Movies;
