import React from "react";
import Form from "./common/form";
import Joi from "joi-browser";
import { saveMovie } from "./services/fakeMovieService";

class MovieForm extends Form {
  state = {
    data: {
      title: "",
      genre: "",
      numberInStock: "",
      rate: "",
    },
    errors: {},
  };

  schema = {
    title: Joi.string().required().label("Title"),
    genre: Joi.string().required().label("Genre"),
    numberInStock: Joi.number()
      .min(0)
      .max(100)
      .required()
      .label("Number in Stock"),
    rate: Joi.number().min(0).max(10).required().label("Rate"),
  };

  doSubmit = () => {
    this.props.history.replace("/movies");
  };
  render() {
    const genres = [
      { name: "Action", value: "5b21ca3eeb7f6fbccd471818" },
      { name: "Comedy", value: "5b21ca3eeb7f6fbccd471814" },
      { name: "Thriller", value: "5b21ca3eeb7f6fbccd471820" },
    ];
    return (
      <React.Fragment>
        <h1>Movie Form {this.props.match.params.id} </h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderInput("genre", "Genre", "select", genres)}
          {this.renderInput("numberInStock", "Number in Stock")}
          {this.renderInput("rate", "Rate")}
          {this.renderButton("Save")}
        </form>
      </React.Fragment>
    );
  }
}

export default MovieForm;
